import copyimport randomfrom tinydb import TinyDB, Queryfrom Tournament_manager.controllers.players_controller import PlayersControllerfrom Tournament_manager.models.tournament import Tournamentfrom Tournament_manager.models.round import Roundfrom Tournament_manager.models.match import Matchfrom Tournament_manager.views.interface import Interfaceclass TournamentController:    def __init__(self):        self.tournament = None        self.round_ = None        self.match = None        self.finished_matches_list = []        self.players_controller = None        self.players_list = []        self.loaded_history_matches = []    def run(self):        Interface.introduction()        tournament_name = Interface.ask_tournament_name()        tournament_location = Interface.ask_tournament_location()        tournament_start_date = str(Interface.ask_tournament_start_date())        tournament_end_date = str(Interface.ask_tournament_end_date())        number_of_round = Interface.ask_round()        self.players_controller = PlayersController(Interface.ask_number_of_players())        self.players_controller.get_data_players()        self.players_list = self.players_controller.players_list        players_id = []        for player in self.players_list:            players_id = [player.chess_national_id]        self.tournament = Tournament(tournament_name,                                     tournament_location,                                     tournament_start_date,                                     tournament_end_date,                                     number_of_round,                                     players_id,                                     )        current_match = False        Interface.started_tournament(self.tournament.tournament_name)        self.start_tournament(current_match)    def speed_run(self):        Interface.second_introduction()        tournament_name = "Quick tournament"        tournament_location = "Paris"        number_of_round = 2        number_of_players = 4        tournament_start_date = "00-00-0000"        tournament_end_date = "00-00-0001"        self.players_controller = PlayersController(number_of_players)        self.players_list = self.players_controller.quick_do_players_list()        players_id = []        for player in self.players_list:            players_id.append(player.chess_national_id)        self.tournament = Tournament(tournament_name,                                     tournament_location,                                     tournament_start_date,                                     tournament_end_date,                                     number_of_round,                                     players_id,                                     )        current_match = False        Interface.started_tournament(self.tournament.tournament_name)        self.start_tournament(current_match)    def start_tournament(self, current_match: bool):        """Star tournament with the new matches or loaded matches according to argument 'current_match'.        Args :            current_match: Type Boolean. Determine if a match has been loaded.        """        history_pairs = self.finished_matches_list        self.tournament.round_history = []        self.tournament.save_tournament_db()        for nb_round in range(1, self.tournament.number_of_round + 1):            self.loaded_history_matches = []            self.round_ = Round(nb_round, matches=None, matches_list=None, round_list=None)            self.define_players_for_match(nb_round, history_pairs, current_match)            self.round_.save_round_db()            self.round_.get_data_round_db()            self.tournament.round_history.append(self.round_.id_last_round)            self.tournament.update_tournament()        self.tournament.update_tournament()        self.define_tournament_winner()    def define_players_for_match(self, nb_round: int, history_pairs: list, current_match: bool):        """Define players for matches.            Save history pairs to avoid duplication of matches and save the tournament        Args :            nb_round : Round number in tournament            history_pairs : played marches for history            current_match : determine if a match has been loaded        """        loaded_current_matches = []        id_matches_list = []        players_list_copy = copy.deepcopy(self.players_list)        Interface.display_round(nb_round)        while players_list_copy:            copied_player_1 = None            copied_player_2 = None            if current_match:                loaded_current_matches.append(self.match.player_1)                loaded_current_matches.append(self.match.player_2)                for copied_player in players_list_copy:                    if copied_player.chess_national_id == self.match.player_1.chess_national_id:                        copied_player_1 = copied_player                    elif copied_player.chess_national_id == self.match.player_2.chess_national_id:                        copied_player_2 = copied_player                current_match = False            else:                copied_player_1 = players_list_copy.pop(0)                for player in players_list_copy:                    if (copied_player_1.chess_national_id, player.chess_national_id) not in history_pairs and (                            player.chess_national_id, copied_player_1.chess_national_id) not in history_pairs:                        copied_player_2 = player                        break            chess_national_id_players = (copied_player_1.chess_national_id, copied_player_2.chess_national_id)            history_pairs = self.save_history_pairs(chess_national_id_players)            pairs_of_players = self.map_players(copied_player_1, copied_player_2)            self.match = Match(pairs_of_players[0], pairs_of_players[1], winner=None)            self.round_.matches = self.match            # self.round_.save_round_db()            self.ask_match_situation()            self.match.save_match_db()            id_matches_list.append(Match.get_data_matches_db())            # self.round_.id_matches_list = Match.get_data_matches_db()            self.round_.matches = [self.loaded_history_matches]            players_list_copy.remove(copied_player_2)            if self.players_controller is None:                self.players_controller = PlayersController()        self.round_.id_matches_list = id_matches_list    def map_players(self, copied_player_1: object, copied_player_2: object) -> object:        """map players between original list and copied list with chess national ID data        Arg:            copied_player_1: Copied player 1            copied_player_2: Copied player 2        Returns:            List of original players ( Object)        """        player_1 = None        player_2 = None        while player_1 is None and player_2 is None:            for player in self.players_list:                if player.chess_national_id == copied_player_1.chess_national_id:                    player_1 = player                elif player.chess_national_id == copied_player_2.chess_national_id:                    player_2 = player                    break                else:                    continue        return [player_1, player_2]    def save_history_pairs(self, pair_of_players: tuple):        self.finished_matches_list.append(pair_of_players)        history_pairs = self.finished_matches_list        return history_pairs    def ask_match_situation(self):        Interface.display_match(self.match)        answer = Interface().ask_match_winner()        if answer == 4:            self.match.save_match_db()            self.round_.id_matches_list.append(Match.get_data_matches_db())            self.round_.save_round_db()            self.round_.get_data_round_db()            # self.tournament.round_history.append(self.round_.id_last_round)            self.tournament.update_tournament()            print("You have stopped the tournament.")            exit()        else:            self.match.define_match_winner(answer)            self.match.player_1.update_players_score()            self.match.player_2.update_players_score()    def define_tournament_winner(self):        self.players_list.sort(key=lambda x: x.score, reverse=True)        print(f"The tournament winner is: {self.players_list[0]} with "              f"{self.players_list[0].score} points")        Interface.players_ranking(self.tournament.tournament_name, self.players_list)    def load_tournament(self):        requested_tournament = Interface.ask_tournament_for_to_load()        self.check_loaded_tournament_name(requested_tournament)        self.tournament = Tournament.from_dict(requested_tournament)        loaded_players = PlayersController()        self.players_list = loaded_players.get_db_data_players(self.tournament.players_id)        loaded_round = self.tournament.rounds        self.round_ = Round.from_dict(loaded_round)        loaded_history_matches = []        loaded_current_match = []        matches_id = self.round_.matches        for id_ in matches_id:            match_preparation = Match.from_dict(id_)            if match_preparation.winner == None:                for player in self.players_list:                    if player.chess_national_id == match_preparation.player_1:                        loaded_current_match.append(player)                    if player.chess_national_id == match_preparation.player_2:                        loaded_current_match.append(player)                self.match = Match(loaded_current_match[0], loaded_current_match[1], winner=None)                current_match = True            else:                for player in self.players_list:                    if player.chess_national_id == match_preparation.player_1:                        prepared_player_1 = match_preparation.player_1                    if player.chess_national_id == match_preparation.player_2:                        prepared_player_2 = match_preparation.player_2                loaded_history_match = (prepared_player_1, prepared_player_2)                loaded_history_matches.append(loaded_history_match)                current_match = False        self.finished_matches_list = loaded_history_matches        self.round_.matches = loaded_current_match        self.start_tournament(current_match)        Interface.started_tournament(self.tournament.tournament_name)    def check_loaded_tournament_name(self, tournament_name):        """Check if a tournament has been played        :param tournament_name: Tournament name        :return:        """        requested_tournament = Tournament.search_tournament(tournament_name)        while True:            if requested_tournament:                return requested_tournament            else:                answer = Interface.report_tournament_error(tournament_name)                if answer == "t":                    self.load_tournament()                elif answer == "e":                    exit()                else:                    print("Not valid answer ")